        and update_by = #{updateBy,jdbcType=VARCHAR}


        and update_time = #{updateTime,jdbcType=TIMESTAMP}


        and remark = #{remark,jdbcType=VARCHAR}

select

from device


        and id = #{id,jdbcType=BIGINT}




        and app_id = #{appId}


        and client_id like concat('%', #{clientId}::varchar, '%')


        and device_identification like concat('%', #{deviceIdentification}::varchar, '%')


        and device_name like concat('%', #{deviceName}::varchar, '%')


        and device_status = #{deviceStatus}


        and connect_status = #{connectStatus}


        and is_will = #{isWill}


        and product_identification = #{productIdentification}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and device_version = #{deviceVersion}


        and device_sn = #{deviceSn}


        and ip_address = #{ipAddress}


        and activated_time = #{activatedTime}


        and last_online_time = #{lastOnlineTime}


        and mac_address = #{macAddress}


        and active_status = #{activeStatus}


        and parent_identification = #{parentIdentification}


        and device_type = #{deviceType}


        and parent_identification is not null


        and (parent_identification is null or parent_identification = '')


order by update_time desc

